<script>
    import {
        mapState,
        mapMutations
    } from 'vuex'

    export default {
        name: "asset",
        data: function() {
            return {
                message: "",
                signMsg: "",
                UserInfo: "",
                flag: !1,
                flag1: !1,
                isSign: !0
            }
        },
        computed: mapState(['Config', 'StateCode', 'notice', 'plantformInfo', 'shopInfo', 'userInfo', 'hasLogin']),
        onLoad: function(options) {
            uni.setNavigationBarTitle({
                title:this.__('')
            });

            var that = this;


            this.forceUserInfo(function(user) {
                that.setData({
                    UserInfo: user
                });
                that.load();
            });

        },
        methods: {
            ...mapMutations(['login', 'logout', 'getPlantformInfo', 'forceUserInfo', 'getUserInfo']),
            load: function() {
                var that = this,
                    params = {
                        user_id: this.userInfo.user_id,
                    };

                that.$.request({
                    url: this.Config.URL.user.signState,
                    data: params,
                    success: function(data, status, msg, code) {
                        that.setData({
                            isSign: data.state == 250 ? 1 : 0
                        })
                    }
                });


                that.setData({
                    message: this.userInfo
                })
                /*
                that.getUserData(function(user_data) {
                    that.getUserResource(function(resource) {
                        resource['user_level_name'] = user_data.member_info.user_level_name
                        resource['user'] = user_data

                        if (that.$.isNull(resource.user_growth)) {
                            resource.user_growth = 0;
                        }

                        that.setData({
                            message: resource
                        })
                    });
                });
                */

            },

            click: function() {
                var that = this,
                    param = {
                        user_id: this.userInfo.user_id,
                        store_id: this.shopInfo.store_id
                    };

                that.$.request({
                    url: this.Config.URL.user.signIn,
                    data: param,
                    success: function(data, status, msg, code) {

                        that.setData({
                            flag: !that.$.isNull(data)
                        });
                        that.setData({
                            signMsg: msg
                        });
                        if (200 == status) {
                            that.setData({
                                flag: !0
                            });
                            setTimeout(function() {
                                that.setData({
                                    flag: !1
                                })
                            }, 2e3)
                        } else {
                            that.setData({
                                flag1: !0
                            });
                            setTimeout(function() {
                                that.setData({
                                    flag1: !1
                                })
                            }, 2e3);
                        }

                        that.load()
                    }
                });
            }

        }
    };
</script>


